const initialstate={
    user: {
        id: "",
        name:"",

    }
}

const reducer=(state=initialstate,action)=>{
    switch(action.type){
        case 'USER_LOGIN':
            return {
                ...state,
                ...user,
                id:action.id 
            }
        default:
            return state;
    }
}

export default reducer;
